import useD3 from "../../hooks/useD3";
import {d3CircularPacking} from "../../d3/D3CircularPacking";
import './style.scss'
import {useEffect, useMemo, useState} from "react";
import {InputNumber, message} from "antd";

const messageText: string = "Hi from Davide! Try to drag circles in the canvas"

const CircularPacking = () => {
    const [selectedNumber, setSelectedNumber] = useState<1 | 1000>(1)
    const numberOfCircle = useMemo(() => Number(selectedNumber), [selectedNumber])

    const ref = useD3((svg) => d3CircularPacking(svg, numberOfCircle), [numberOfCircle]);
    const handleChange = (value:  1 | 1000 | null) => {
        if(value !== null) {
            setSelectedNumber(value)
        }
    };

    const [messageApi, contextHolder] = message.useMessage();
    useEffect(() => {
        messageApi.info({content: messageText, key: "message"});
        return () => { messageApi.destroy("message") }
    })
    return (
        <>
            {contextHolder}
            <InputNumber min={1} max={1000} onChange={handleChange} defaultValue={selectedNumber} style={{ width: '100%' }}/>
            <svg ref={ref} className="img"/>
        </>)
}

export default CircularPacking
