import useD3 from "../../hooks/useD3";
import {d3CircularPacking} from "../../d3/D3CircularPacking";
import './style.scss'
import React, {useEffect, useMemo, useState} from "react";
import {InputNumber} from "antd";
import InfoMessage from "../../components/InfoMessage";
import {Range} from "../../interfaces/range";

const message = `Welcome from Davide, try to insert a number between 1 and 1000 to see more interactive circles`

const CircularPacking = () => {
    const [selectedNumber, setSelectedNumber] = useState<Range<1,1000>>(999)
    const numberOfCircle = useMemo(() => Number(selectedNumber), [selectedNumber])

    const ref = useD3((svg) => d3CircularPacking(svg, numberOfCircle), [numberOfCircle]);
    const handleChange = (value:  1 | 999 | null) => {
        if(value !== null) {
            setSelectedNumber(value)
        }
    };

    const handleStep = (value: 1 | 1000, info: {type: 'up' | 'down' }) => {
        const newValue = value * value >= 1000 ? 1000 : value * value
        switch (info.type) {
            case "up":
                break;
            case "down":
                break;

        }
        // if( info.type === 'up') {
        //     setSelectedNumber((prevValue: 1 | 1000) => {
        //
        //         if (newValue <= 1000) {
        //             return newValue
        //         } else {
        //             return 1000
        //         }
        //     })
        // } else if(info.type === 'down')  {
        //
        // }
    }


    return (
        <>
            <InfoMessage content={message}/>
            <InputNumber
                min={1}
                max={1000}
                onChange={handleChange}
                defaultValue={selectedNumber}
                style={{ width: '100%' }}
                onStep={handleStep}
            />
            <svg ref={ref} className="img"/>
        </>)
}

export default CircularPacking
