import React, {useRef} from "react";
import {Card, Divider, Layout, Typography} from 'antd';
import './style.scss'
import {index} from "d3";

const { Title } = Typography;

const textStyle: React.CSSProperties = {
    textAlign: 'center'
};

const { Header, Content} = Layout;
const usePersonalizedHook = () => useRef(null)
const LayoutComp: React.FC<ILayoutComp> = ({components}) => {
    const refElements = useRef<HTMLDivElement[]>([])
    function handleScrollToSecondCat(index: number) {
        if(refElements.current && refElements.current[index]!== null) {
            refElements.current[index].scrollIntoView({
                behavior: 'smooth',
                block: 'nearest',
                inline: 'center'
            });
        }
    }
    return(
        <Layout className="container">
            <Header className="header">
                {components.map(({title}, index) => (
                    <>
                        <a href="#" onClick={()=> handleScrollToSecondCat(index)}>{title}</a>
                        <Divider type={"vertical"} style={{height: "100%"}} />
                    </>
                    ))}
            </Header>
            <Content className="main">
                {components.map(({title, content, subtitle}, index  ) => (
                    // @ts-ignore
                    <div key={index.toString()} className={`content content-${index.toString()}`} ref={(el) => refElements.current[index] = el}>
                        <Title style={textStyle}>{title}</Title>
                        {subtitle ? <Title style={textStyle} level={2}>{subtitle}</Title> : <></>}
                        {content}
                    </div>
                ))}
            </Content>
            <Card bordered={false} className="aside aside-1" />
            <Card bordered={false} className="aside aside-2" />
            {/*<Footer className="footer" />*/}
        </Layout>)
}

type ILayoutComp = {
    components: {
        title: string;
        subtitle?: string;
        content?: JSX.Element;
    }[]
}


export default LayoutComp
