import useD3 from "../../hooks/useD3";
import {d3CircularPacking} from "../../d3/D3CircularPacking";
import './style.scss'
import {ChangeEvent, EventHandler, useCallback, useState} from "react";
import {Input} from "antd";
const CircularPacking = () => {

    const [selectedNumber, setSelectedNumber] = useState("0")
    const numberOfCircle = useCallback((i: number) => i+Number(selectedNumber), [selectedNumber])
    const ref = useD3((svg, totalCircles) => d3CircularPacking(svg,totalCircles), numberOfCircle);
    console.log({selectedNumber})
    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { value: inputValue } = e.target;
        const reg = /^-?\d*(\.\d*)?$/;
        if (reg.test(inputValue) || inputValue === '') {
            setSelectedNumber(inputValue);
        }
    };
    return (<>
        <Input value={selectedNumber} onChange={handleChange} /><svg ref={ref} className="img"/></>)
}

export default CircularPacking
